parameters.inputSchema:
{
	"type": "object",
	"properties": {
		"departments_needed": {
			"type": "array",
            "description": "List of departments we need to hire for this role",
			"items": {
				"type": "string",
                "description": "Agent"
            },
            "minItems": 1,
            "maxItems": 7
		},
		"agents_needed": {
			"type": "array",
            "description": "List of agents we need to hire for this role",
			"items": {
				"type": "string",
                "description": "Agent"
            },
            "minItems": 1,
            "maxItems": 10
		},
		"plan": {
			"type": "array",
            "description": "step by step which agent takes on which role",
			"items": {
				"type": "string",
                "description": "step in the plan defining which agent takes on which role. E.g. Copywriter: write 10 ads"
			},
            "minItems": 7,
            "maxItems": 15
		}
	}
}
